    int sum = 0;
    for (int i = 0; i 

当完成一帧数据的接收后程序会调用procParse 解析

    // 支持checksum校验，需要时在CommDef.h文件中打开PRO_SUPPORT_CHECK_SUM宏
#ifdef PRO_SUPPORT_CHECK_SUM
    // 检测校验码
    if (getCheckSum(pData, frameLen - 1) == pData[frameLen - 1]) {
        // 解析一帧数据
        procParse(pData, frameLen);
